import './index.scss';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import AnimatedLetters from '../AnimatedLetter';
import Loader from 'react-loaders';
import 'loaders.css/loaders.css';
import LogoS from '../../assets/images/frontend_vector.svg';

const Home = () => {
    const [letterClass, setLetterClass] = useState('text-animate');
    const [animatedText, setAnimatedText] = useState('');
    const [jobText, setJobText] = useState('');
    const nameArray = [' ', 'C', 'h', 'a', 'r', 'a', 'n'];
    const jobTitles = [
        'Frontend Developer',
        'React Developer',
        'Full Stack Developer'
    ];
    const [currentJobIndex, setCurrentJobIndex] = useState(0);

    useEffect(() => {
        setTimeout(() => {
            setLetterClass('text-animate-hover');
        }, 4000);
    }, []);

    useEffect(() => {
        let index = 0;
        const targetText = "From Pixels to Performance: Full Stack Magic Begins Here...";
        const interval = setInterval(() => {
            setAnimatedText(targetText.slice(0, index));
            index++;

            if (index > targetText.length) {
                clearInterval(interval);
            }
        }, 100);

        return () => clearInterval(interval);
    }, []);

    useEffect(() => {
        const typeWriter = (text, i, callback) => {
            if (i < text.length) {
                setJobText((prev) => prev + text.charAt(i));
                setTimeout(() => typeWriter(text, i + 1, callback), 150);
            } else if (callback) {
                setTimeout(callback, 1500);
            }
        };

        const startTyping = () => {
            setJobText('');
            typeWriter(jobTitles[currentJobIndex], 0, () => {
                setCurrentJobIndex((prevIndex) => (prevIndex + 1) % jobTitles.length);
            });
        };

        startTyping();
    }, [currentJobIndex]);

    return (
        <>
            <div className="container home-page">
                <div className="text-zone">
                    <h1>
                        <span className={letterClass}>H</span>
                        <span className={`${letterClass} _12`}>i</span>
                        <br />
                        <span className={`${letterClass} _13`}>i</span>
                        <span className={`${letterClass} _14`}>'m </span>
                        <AnimatedLetters
                            letterClass={letterClass}
                            strArray={nameArray}
                            idx={15}
                        />
                        <br />
                        <AnimatedLetters
                            letterClass={letterClass}
                            strArray={jobText.split('')}
                            idx={22}
                        />
                    </h1>
                    <h2>frontend developer / React / HTML / CSS / Bootstrap / Canva / Figma</h2>
                    <h2 className='movin_text'>{animatedText}</h2>
                    <Link to="/contact" className='flat-button'> CONTACT ME </Link>
                </div>
                <div className="logo-container">
                    <img
                        className="solid-logo"
                        src={LogoS}
                        alt="JavaScript, Developer"
                    />
                </div>
            </div>
            <Loader type="ball-pulse" active={true} />
        </>
    );
};

export default Home;
