import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngular, faCss3, faGitAlt, faHtml5, faJsSquare, faReact } from '@fortawesome/free-brands-svg-icons';
import Loader from 'react-loaders';
import AnimatedLetters from '../AnimatedLetter';
import backendImg from '../../assets/images/backend.png';
import java from '../../assets/images/java.svg';
import html from '../../assets/images/html.svg';
import javascript from '../../assets/images/javascript.svg';
import react from '../../assets/images/react.svg';
import pyImage from '../../assets/images/pyImage.png';
import './index.scss';

const About = () => {
  const [letterClass, setLetterClass] = useState('text-animate');
  const about_arr = ['A', 'b', 'o', 'u', 't', ' ', 'm', 'e'];

  useEffect(() => {
    setTimeout(() => {
      setLetterClass('text-animate-hover');
    }, 4000);
  }, []);

  return (
    <>
      <div className="container about-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters letterClass={letterClass} strArray={about_arr} idx={15} />
          </h1>
          <p>
            I'm very ambitious fullstack developer looking for a role in established IT company with the opportunity to
            work with the latest technologies on challenging and diverse projects.
          </p>
          <p>
            I'm quietly confident, naturally curious, and perpetually working on improving my chops one design problem
            at a time.
          </p>
          <p>
            If I need to define myself in one sentence that would be a family person, Son of beautiful parents, a
            sports fanatic, photography enthusiast, and tech-obsessed!!!
          </p>
        </div>

        {/* Skills Section */}
        <div className="skills-section">
          <h2>Skills</h2>
          {/* Add your skill icons or images here */}
          <div className="stage-cube-cont">
            <div className="cubespinner">
              <div className="face1">
                <FontAwesomeIcon icon={faAngular} color="#DD0031" />
              </div>
              <div className="face2">
                <FontAwesomeIcon icon={faHtml5} color="#F06529" />
              </div>
              <div className="face3">
                <FontAwesomeIcon icon={faCss3} color="#28A4D9" />
              </div>
              <div className="face4">
                <FontAwesomeIcon icon={faReact} color="#5ED4F4" />
              </div>
              <div className="face5">
                <FontAwesomeIcon icon={faJsSquare} color="#EFD81D" />
              </div>
              <div className="face6">
                <FontAwesomeIcon icon={faGitAlt} color="#EC4D28" />
              </div>
            </div>
          </div>
        </div>
      </div>

      <Loader type="semi-circle-spin" active={true} />
    </>
  );
};

export default About;
