import './index.scss';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { Typewriter } from 'react-simple-typewriter';
import AnimatedLetters from '../AnimatedLetter';
import Loader from 'react-loaders';
import 'loaders.css/loaders.css';
import LogoS from '../../assets/images/frontend_vector.svg';

const Home = () => {
    const [letterClass, setLetterClass] = useState('text-animate');
    const [targetText, setTargetText] = useState('');
    const nameArray = [' ', 'C', 'h', 'a', 'r', 'a', 'n'];

    useEffect(() => {
        setTimeout(() => {
            setLetterClass('text-animate-hover');
        }, 2000);
    }, []);

    useEffect(() => {
        let index = 0;
        const targetTextString = "From Pixels to Performance: Full Stack Magic Begins Here...";
        const interval = setInterval(() => {
            setTargetText(targetTextString.slice(0, index));
            index++;

            if (index > targetTextString.length) {
                clearInterval(interval);
            }
        }, 100);

        return () => clearInterval(interval);
    }, []);

    return (
        <>
            <div className="container home-page">
                <div className="text-zone">
                    <h1>
                        <span className={letterClass}>H</span>
                        <span className={`${letterClass} _12`}>i</span>
                        <br />
                        <span className={`${letterClass} _13`}>i</span>
                        <span className={`${letterClass} _14`}>'m </span>
                        <AnimatedLetters
                            letterClass={letterClass}
                            strArray={nameArray}
                            idx={15}
                        />
                        <br />
                        <Typewriter
                            words={['Frontend Developer', 'React Developer', 'Full Stack Developer']}
                            loop={0}
                            cursor
                            cursorStyle='_'
                            typeSpeed={70}
                            deleteSpeed={50}
                            delaySpeed={1000}
                        />
                    </h1>
                    <h2>frontend developer / React / HTML / CSS / Bootstrap / Canva / Figma</h2>
                    <h2 className='movin_text'>{targetText}</h2>
                    <Link to="/contact" className='flat-button'> CONTACT ME </Link>
                </div>
                <div className="logo-container">
                    <img
                        className="solid-logo"
                        src={LogoS}
                        alt="JavaScript, Developer"
                    />
                </div>
            </div>
            <Loader type="ball-pulse" active={true} />
        </>
    );
};

export default Home;
