import React, { useEffect, useState } from 'react';
import AnimatedLetters from '../AnimatedLetter';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngular, faCss3, faGitAlt, faHtml5, faJsSquare, faReact } from '@fortawesome/free-brands-svg-icons';
import Loader from 'react-loaders';

import backendImg from '../../assets/images/backend.png';
import java from '../../assets/images/java.svg';
import html from '../../assets/images/html.svg';
import javascript from '../../assets/images/javascript.svg';
import react from '../../assets/images/react.svg';
import pyImage from '../../assets/images/pyImage.png';

const About = () => {
  const [letterClass, setLetterClass] = useState('text-animate');
  const about_arr = ['A', 'b', 'o', 'u', 't', ' ', 'm', 'e'];

  useEffect(() => {
    setTimeout(() => {
      setLetterClass('text-animate-hover');
    }, 4000);
  }, []);

  return (
    <>
      <div className="container about-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters letterClass={letterClass} strArray={about_arr} idx={15} />
          </h1>
          <p>
            I'm very ambitious fullstack developer looking for a role in established IT company with the opportunity to work
            with the latest technologies on challenging and diverse projects.
          </p>
          <p>
            I'm quietly confident, naturally curious, and perpetually working on improving my chops one design problem at a
            time.
          </p>
          <p>
            If I need to define myself in one sentence that would be a family person, Son of beautiful parents, a sports
            fanatic, photography enthusiast, and tech-obsessed!!!
          </p>
        </div>

        <div className="stage-cube-cont">
          <div className="cubespinner">
            {/* Skill cards */}
            {[
              { icon: faAngular, color: '#DD0031', image: java, label: 'Java' },
              { icon: faHtml5, color: '#F06529', image: html, label: 'HTML & CSS' },
              { icon: faCss3, color: '#28A4D9', image: javascript, label: 'JavaScript' },
              { icon: faReact, color: '#5ED4F4', image: react, label: 'ReactJs' },
              { icon: faJsSquare, color: '#EFD81D', image: pyImage, label: 'Python' },
            ].map((skill, index) => (
              <div key={index} className="face">
                <FontAwesomeIcon icon={skill.icon} color={skill.color} />
                <h3 className="flex items-center text-[20px]">
                  <img className="h-[40px] w-[40px] items-center ml-12 mr-5" src={skill.image} alt="" />
                  {skill.label}
                </h3>
              </div>
            ))}
          </div>
        </div>

        <Loader type="semi-circle-spin" active={true} />
      </div>
    </>
  );
};

export default About;
